import React, { useState } from "react";
import "./Login.css";
import { Link } from "react-router-dom";

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [modal, setModal] = useState({ isOpen: false, title: "", message: "" });

  const handleLogin = (e) => {
    e.preventDefault();

    // Get existing accounts from localStorage
    const existingAccounts = JSON.parse(localStorage.getItem("accounts")) || [];

    // Check if the username and password match an account
    const account = existingAccounts.find(
      (account) =>
        (account.username === usernameOrEmail ||
          account.email === usernameOrEmail) &&
        account.password === password
    );

    if (account) {
      // Login successful
      console.log("Login successful");
      setError("");
      // You can set the logged-in user's data in local storage or redirect to another page
      localStorage.setItem("loggedInUser", JSON.stringify(account));
    } else {
      setError("Invalid username or password");
    }
  };

  return (
    <div>
      <div className="login">
        <h1>Login</h1>
        <form onSubmit={handleLogin}>
          <div class="input-group">
            <input
              type="text"
              id="username"
              name="username"
              placeholder="Email or username*"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div class="input-group">
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Password*"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div class="input-group remember-me">
            <input type="checkbox" id="remember" name="remember" />
            <label for="remember">Remember Me</label>
          </div>
          <button type="submit">Login</button>
          <a className="lost-pass" href="#">
            Lost your password?
          </a>
          <p>
            Don't have an account? <Link to="/register">Register here</Link>.
          </p>
        </form>
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
}

export default Login;
