import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./DeleteAccount.css";

const DeleteAccount = () => {
  const [reason, setReason] = useState("");
  const [confirm, setConfirm] = useState(false);
  const navigate = useNavigate();

  const handleDelete = () => {
    if (confirm) {
      const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
      console.log("Logged-in user:", loggedInUser);

      const allUsers = JSON.parse(localStorage.getItem("users")) || [];
      console.log("All users before deletion:", allUsers);

      const updatedUsers = allUsers.filter(
        (user) => user.username !== loggedInUser.username
      );
      console.log("All users after deletion:", updatedUsers);

      localStorage.setItem("users", JSON.stringify(updatedUsers));
      localStorage.removeItem("loggedInUser");

      navigate("/");
    } else {
      alert("Please confirm account deletion.");
    }
  };

  return (
    <div className="delete-account-page">
      <h2>Delete Your Account</h2>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleDelete();
        }}
      >
        <div className="form-group">
          <label htmlFor="reason">Reason for Deleting Account:</label>
          <textarea
            id="reason"
            value={reason}
            onChange={(e) => setReason(e.target.value)}
            required
          />
        </div>
        <div className="form-group-checkbox">
          <label className="checkbox">
            <input
              type="checkbox"
              checked={confirm}
              onChange={(e) => setConfirm(e.target.checked)}
            />
            I confirm that I want to delete my account.
          </label>
        </div>
        <button type="submit" className="delete-button">
          Delete Account
        </button>
      </form>
    </div>
  );
};

export default DeleteAccount;
