import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./DeleteAccount.css";

const DeleteAccount = () => {
  const [reason, setReason] = useState("");
  const [confirm, setConfirm] = useState(false);
  const navigate = useNavigate();

  const handleDelete = () => {
    if (confirm) {
      // Retrieve the currently logged-in user
      const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
      if (!loggedInUser) {
        alert("No user is currently logged in.");
        return;
      }

      // Retrieve all accounts from localStorage
      const allAccounts = JSON.parse(localStorage.getItem("accounts")) || [];

      // Filter out the current logged-in user from the accounts array
      const updatedAccounts = allAccounts.filter(
        (account) => account.username !== loggedInUser.username
      );

      // Update the accounts array in localStorage
      localStorage.setItem("accounts", JSON.stringify(updatedAccounts));

      // Remove all user-specific data from localStorage
      localStorage.removeItem("loggedInUser"); // Remove the logged-in user key

      // You can add more keys here if your application stores more data related to the user

      // Confirm the deletion
      alert("Your account and all associated data have been deleted.");

      // Redirect to the homepage after account deletion
      navigate("/");
    } else {
      alert("Please confirm account deletion.");
    }
  };

  return (
    <div className="delete-account-page">
      <h2>Delete Your Account</h2>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleDelete();
        }}
      >
        <div className="form-group">
          <label htmlFor="reason">Reason for Deleting Account:</label>
          <textarea
            id="reason"
            value={reason}
            onChange={(e) => setReason(e.target.value)}
            required
          />
        </div>
        <div className="form-group-checkbox">
          <label className="checkbox">
            <input
              type="checkbox"
              checked={confirm}
              onChange={(e) => setConfirm(e.target.checked)}
            />
            I confirm that I want to delete my account.
          </label>
        </div>
        <button type="submit" className="delete-button">
          Delete Account
        </button>
      </form>
    </div>
  );
};

export default DeleteAccount;
